import React from 'react';
import {
  reactExtension,
  useApi,
  ActionItem,
} from '@shopify/ui-extensions-react/point-of-sale';

const PostPurchaseActionItem = () =&gt; {
  const api = useApi&lt;'pos.purchase.post.action.menu-item.render'&gt;();

  return (
    &lt;ActionItem
