export default extension(
  'pos.purchase.post.action.menu-item.render',
  (root, api) =&gt; {
    const actionItem = root.createComponent(ActionItem, {
      onPress: () =&gt; api.action.presentModal(),
      enabled: true,
    });

    root.append(actionItem);
  },
);

