const onPinSubmit = (pin: number[]): Promise&lt;PinValidationResult&gt; =&gt; {
  return new Promise((resolve) =&gt; {
    setTimeout(() =&gt; {
      const isPinValid =
        pin.length === 6 && pin.every((digit, index) =&gt; digit === index + 1);
      const result: PinValidationResult = isPinValid ? 'accept' : 'reject';
      resolve(result);
    }, 1000);
  });
