  reactExtension,
} from '@shopify/ui-extensions-react/point-of-sale';

const SmartGridModal = () =&gt; {
  const [date, setDate] = useState(
    new Date().toDateString(),
  );
  const visibleState = useState(false);

  return (
    &lt;Screen name="Home" title="Home"&gt;
      &lt;Text&gt;Selected date: {date}&lt;/Text&gt;
      &lt;Button
        title="Show"
        onPress={() =&gt; {
          visibleState[1](true);
        }}
      /&gt;
      &lt;TimePicker
        visibleState={visibleState}
        onChange={(selected) =&gt; {
          setDate(
            new Date(selected).toDateString(),
          );
        }}
        selected={date}
        is24Hour={true}
        inputMode={'spinner'}
      /&gt;
    &lt;/Screen&gt;
  );
};

export default reactExtension(
  'pos.home.modal.render',
  () =&gt; &lt;SmartGridModal /&gt;,
);

import {
  Button,
  TimePicker,
  Screen,
  extension,
} from '@shopify/ui-extensions/point-of-sale';

export default extension(
  'pos.home.modal.render',
  (root) =&gt; {
    const mainScreen = root.createComponent(
      Screen,
      {
        name: 'Home',
        title: 'Home',
      },
    );

    const button = root.createComponent(Button, {
      title: 'Show',
      onPress: () =&gt; {
        timePicker.updateProps({
          visibleState: [true, () =&gt; {}],
        });
      },
    });

    const handleOnChange = (selected) =&gt; {
      timePicker.updateProps({
        selected: new Date(
          selected,
        ).toDateString(),
      });
    };

    const timePicker = root.createComponent(
      TimePicker,
      {
        visibleState: [false, () =&gt; {}],
        onChange: handleOnChange,
        selected: new Date().toDateString(),
        is24Hour: true,
        inputMode: 'spinner',
      },
    );

    mainScreen.append(button);
    mainScreen.append(timePicker);
    root.append(mainScreen);
  },
);

